mapper.select:
SELECT * FROM (
SELECT ROWNUM AS rnum, A.*
FROM (
SELECT *
FROM prod





				PROD_NAME LIKE '%' || #{sword} || '%'




				PROD_CONTENT LIKE '%' || #{sword} || '%'




				(PROD_NAME LIKE '%' || #{sword} || '%' OR PROD_CONTENT LIKE '%' || #{sword} || '%')




ORDER BY PROD_ID DESC
) A
WHERE ROWNUM <= #{end}
)
WHERE rnum >= #{start}
